input = input.split("")

# PART 1.
# To what floor do the instructions take Santa?

sum = 0
input.each_with_index do |step, i|
  direction = (step == "(" ? 1 : -1)
  sum = sum + direction
end
puts "To what floor do the instructions take Santa? " + sum.to_s

# PART 2.
# What is the position of the character that causes Santa to first enter the basement?

sum = 0
index = ""

input.each_with_index do |step, i|
  direction = (step == "(" ? 1 : -1)
  if sum == -1
    index = i
    break
  else
    sum = sum + direction
  end
end

puts "What is the position of the character that causes Santa to first enter the basement? " + index.to_s
