def marker_detector(input)
  new_ary = []
  input.split("").each.with_index do |char, i|
    new_ary << char

    if new_ary.length >= 4
      if new_ary.reverse.slice(0, 4).uniq.length == 4
        puts
        puts "How many characters need to be processed before the first start-of-packet marker is detected? => #{i + 1}"
        break
      end
    end
    # sleep(1)
    print "."
  end
end

